mapper.update:
UPDATE MEMBER_PET
SET PET_IMG = NULL
WHERE PET_CODE = #{pet_code}

mapper.select:
SELECT PET_IMG
FROM MEMBER_PET
WHERE PET_CODE = #{pet_code}

mapper.update:
UPDATE MEMBER_PET
SET PET_MAIN='N'
WHERE PET_MAIN='Y' AND MEM_CODE=#{param1}

UPDATE MEMBER_PET
SET PET_MAIN='Y'
WHERE PET_CODE=#{param1}

mapper.select:
SELECT PB_OPTION
FROM PET_BREED
WHERE PB_TYPE=#{petType}

SELECT *
FROM MEMBER_PET
WHERE PET_CODE=#{param1}

SELECT *
FROM COUPON
WHERE CP_KEYWORD=#{param1}

SELECT COUNT(*)
FROM MEMBER_COUPON_BRIDGE
WHERE MEM_CODE=#{param1} AND CP_NO=#{param2}

SELECT *
FROM ADDRESS
WHERE MEM_CODE=#{param1}

mapper.update:
UPDATE MEMBER
SET MEM_TELL = #{phoneNumber}
WHERE MEM_CODE = #{memCode}

UPDATE ADDRESS
SET ADDR_DEFAULT = 'Y'
WHERE ADDR_CODE = #{addrCode}

    	UPDATE MEMBER
		SET MEM_EMAIL = #{mem_email},
		    MEM_NAME = #{mem_name},
		    MEM_NICK = #{mem_nick},
		    MEM_BIRTH = #{mem_birth}
		WHERE MEM_CODE = #{mem_code}

    	UPDATE PRODUCT_CART
		SET CART_CNT = #{newQuantity}
		WHERE CART_CODE = #{cartCode}

mapper.select:
SELECT PI.MAIN_IMG1, P.PRO_NAME, PO.PROOPT_NAME, PO.PROOPT_PRICE, P.PRO_DISCOUNT, PO.PROOPT_FINALPRICE, MC.CART_CNT, PO.PROOPT_STOCK, MC.CART_CODE FROM PRODUCT_CART MC LEFT JOIN PRODUCT_IMAGE PI ON MC.PRO_CODE = PI.PRO_CODE LEFT JOIN PRODUCT P ON MC.PRO_CODE = P.PRO_CODE LEFT JOIN PRODUCT_OPTION PO ON MC.PROOPT_CODE = PO.PROOPT_CODE WHERE MC.CART_CODE IN
#{cartCode}
GROUP BY MC.CART_REGDATE, PI.MAIN_IMG1, P.PRO_NAME, PO.PROOPT_NAME, PO.PROOPT_PRICE, P.PRO_DISCOUNT, PO.PROOPT_FINALPRICE, MC.CART_CNT, PO.PROOPT_STOCK, MC.CART_CODE

mapper.update:
    	UPDATE PRODUCT_CART
		SET O_CODE = #{o_code}
		WHERE CART_CODE = #{cartCode}

    	UPDATE MEMBER_COUPON_BRIDGE
	    SET MC_USE = SYSDATE
	    WHERE MC_CODE = #{mc_code}

UPDATE MEMBER
SET MEM_POINT = MEM_POINT - #{o_point} + #{o_saving}
WHERE MEM_CODE = #{mem_code}

mapper.select:
SELECT *
FROM "ORDER"
WHERE MEM_CODE = #{mem_code}

SELECT *
FROM ORDER_STATUS
WHERE O_CODE = #{o_code}

mapper.delete:
DELETE
FROM MEMBER_WISHLIST
WHERE MEM_CODE = #{mem_code} AND PRO_CODE = #{pro_code}

